        private List<Chuongtrinh> createDSCTList()
        {
            return new List<Chuongtrinh>
            {
                new Chuongtrinh(){ID = 1,MST="1", BannerUrl ="resource/images/banner-ct1.jpg",Title="Chương trình học bổng phát triển trẻ em",Content="Giáo dục có thể giúp trẻ em nghèo phát triển khả năng và có khởi đầu công bằng hơn. Tuy nhiên, vẫn còn nhiều trẻ em Việt Nam có ít cơ hội học tập và có nguy cơ bỏ học do nghèo đói và khó khăn cũng như các vấn đề về sức khỏe, địa lý, rào cản kiến thức và khuyết tật..<br/> <br/> Nhờ sự hỗ trợ nhiệt tình của các nhà tài trợ, mỗi năm Chương trình Hỗ trợ Học bổng của saigonchildren trao gần 2.000 học bổng cho trẻ em và thanh thiếu niên thiệt thòi, để các em có thể tiếp tục việc học và phát triển trọn vẹn tiềm năng của mình. Số lượng trẻ trải dài từ cấp tiểu học cho đến bậc đại học. <br/> </br> Chúng tôi áp dụng phương pháp tiếp cận toàn diện để giúp trẻ em và thanh thiếu niên tiếp tục theo học tại trường thông qua một hệ thống gọi là Mạng Lưới Cộng Đồng Hỗ Trợ Phát Triển Trẻ Em. Mạng lưới này sẽ liên kết giáo viên, đại diện địa phương và các bạn trẻ ở cấp địa phương. Qua đó, các thành viên cùng đánh giá nhu cầu của từng trẻ, theo dõi sự tiến bộ của các em cũng như cung cấp cho các em những hỗ trợ liên quan để tiếp cận giáo dục.",ImageUrl="resource/images/project1.png"},
                new Chuongtrinh(){ID = 2,MST="1", BannerUrl ="resource/images/banner-ct2.jpg",Title="Chương trình xây dựng trường học",Content="Mang lại cơ sở vật chất giáo dục phù hợp và tạo điều kiện học tập đầy đủ cho trẻ em có hoàn cảnh thiệt thòi là mối quan tâm của saigonchildren ngay từ những ngày đầu mới thành lập. Chương trình Xây dựng Trường Học và Môi trường Học đường sử dụng các khoảng tài trợ từ phía tư nhân, cùng với những đóng góp từ chính quyền, để xây dựng trường học được sở hữu và vận hành bởi địa phương.<br/> <br/> Ngoài việc đảm bảo sự vững chắc, an toàn và tính thẩm mĩ cho trường, saigonchildren còn xây dựng thêm cơ sở vệ sinh, thư viện, sân chơi và phòng học đặc biệt tùy theo nhu cầu của mỗi địa phương.<br/> <br/> Saigonchildren tận dụng những trường học này cùng với mạng lưới cộng đồng vững chắc để để nâng cao ý thức địa phương về những vấn đề như tính bền cững, bảo vệ môi trường, sức khỏe…<br/> <br/>Từ ngôi trường đầu tiên tại huyện Cần Giờ năm 1993, saigonchildren đã xây dựng hơn 503 phòng học, mở ra tương lai tươi sáng cho hơn 1500 trẻ em có hoàn cảnh thiệt thòi mỗi năm, từ Trà Vinh ở khu vực phía Nam trải dài đến Tuyên Quang ở khu vực miền Bắc.",ImageUrl="resource/images/project2.jpg"},
                new Chuongtrinh(){ID = 3,MST="1", BannerUrl ="resource/images/banner-ct3.jpg",Title="Chương trình đào tạo nghề và kĩ năng",Content="Saigonchildren dùng giáo dục làm phương cách để hỗ trợ trẻ em có hoàn cảnh khó khăn thoát nghèo. Khẳng định tầm quan trọng của giáo dục nghề nghiệp cho trẻ em và thanh thiếu niên có hoàn cảnh khó khăn, Chương Trình Đào Tạo Nghề và Kĩ Năng đã ra đời trên tinh thần đó.<br/> <br/>Qua 25 năm, hơn 2.264.000 USD đã được saigonchildren đầu tư cùng với sự hỗ trợ chuyên môn để hơn 10.000 lượt trẻ em và thanh thiếu niên có hoàn cảnh khó khăn có cơ hội học nghề ngắn hạn, học tiếng Anh, vi tính, kỹ năng sống tại Trường Dạy Tiếng Anh và Dạy nghề Thăng Long để sẵn sàng làm việc hiệu quả.<br/> <br/>Ngoài ra, cơ hội tham gia các hoạt động vui chơi giải trí và các hoạt động xã hội cũng là một phần không thể thiếu của các em trong chương trình này. Góp phần đào tạo trẻ em và thanh thiếu niên thiệt thòi thành những cá nhân tự tin, tự lập, có tay nghề và có trách nhiệm là mục tiêu mà saigonchildren cùng đối tác địa phương đã, đang và sẽ luôn tiếp tục hướng đến.", ImageUrl = "resource/images/project3.jpg"},
                new Chuongtrinh(){ID = 4,MST="1", BannerUrl ="resource/images/banner-ct4.jpg",Title="Chương trình giáo dục đặc biệt",Content="Saigonchildren hỗ trợ trẻ em khuyết tật bằng việc thực hiện các dự án nhằm giúp các em có cuộc sống tốt hơn thông qua việc tiếp cận giáo dục thích hợp và các chương trình đào tạo thuộc chương trình “Giáo dục đặc biệt” (SNEP).<br/> <br/>Saigonchildren còn hợp tác với các tổ chức chuyên môn khắp Việt nam để hỗ trợ các trẻ có những khuyết tật như bại não, khiếm thị, khiếm thính và các khuyết tật khác. Saigonchildren cung cấp các khóa huấn luyện cho các tổ chức địa phương nhằm nhằm nâng cao năng lực của các nhà chuyên môn làm việc với trẻ em khuyết tật. Thông qua các dự án này, nhiều ý tưởng mới, phương cách giáo dục phù hợp được thực hiện, nhiều mô hình giáo dục hiệu quả được nhân rộng, mạng lưới các nhà chuyên môn được kết nối và học hỏi lẫn nhau.<br/> <br/> Saigonchildren tập trung vào mảng giáo dục cho trẻ tự kỉ, bao gồm phát hiện sớm dựa vào cộng đồng, và can thiệp sớm theo mô hình giáo dục đa ngành với sự tham gia của các nhà âm ngữ trị liệu, hoạt động trị liệu, nhà giáo dục và phụ huynh. Điều này được thực hiện thông qua việc kết hợp với rất nhiều tổ chức chuyên môn trong và ngoài nước nhằm phát triển các nguồn tài liệu và các phương pháp trị liệu có bằng chứng khoa học có thể ứng dụng trong bối cảnh Việt Nam, hướng tới việc hình thành một Trung tâm Nguồn và xây dựng một mạng lưới quốc gia về chăm sóc giáo dục trẻ em tự kỷ có chất lượng cao.", ImageUrl = "resource/images/project4.jpg"},
                new Chuongtrinh(){ID = 5,MST ="1", BannerUrl="resource/images/banner-news2.jpg",Title="Trung thu đủ đầy, niềm vui trọn vẹn cho trẻ em Đồng Nai",Content="Giữa mùa hè oi ả, 24 bé nhỏ đang tham gia lớp Can thiệp sớm miễn phí của saigonchildren dành cho trẻ tự kỷ có hoàn cảnh khó khăn, cùng với người chăm sóc và các anh chị em thân thiết, đã cùng nhau hòa mình vào thiên nhiên trong chuyến đi dã ngoại vào cuối tuần qua. <br/> <br/> Năm nay, đồng hành cùng trẻ trong chuyến tham quan còn có các cô chú tình nguyện viên đến từ công ty Intel Việt Nam. Đến với nông trại, các “nông dân nhí” vô cùng phấn khởi khi lần đầu nhìn thấy và chạm vào các loài thú thân thiện được nuôi tại đây. Các bé cũng không ngại chơi với bùn đất, bắt cá bắt ốc, đến thăm vườn rau xanh mát, hay xới đất trồng cây trong những chiếc chậu nhỏ xinh. Các bé sớm trở nên khắng khít và cởi mở với các cô chú sau khi cùng trải qua các trò chơi thú vị trong cả buổi sáng hôm ấy. <br/> <br/> Gác lại giờ nghỉ trưa để nạp năng lượng, các em đều háo hức bước vào hoạt động tô tượng, nặn đất sét, và tự tay gói bánh ít để rèn luyện sự khéo léo và tính kiên nhẫn. Cuối ngày, hàng loạt tiết mục văn nghệ góp vui bởi các bé cũng được trình diễn một cách ngẫu hứng và tràn ngập tiếng cười dưới sự cổ vũ nhiệt tình từ các cô chú tài trợ. Phụ huynh bé M. chia sẻ, “Mọi năm, rất ít có cơ hội cho bé đi chơi ngoài trời với đông bạn bè như thế, và cũng như được gặp nhiều loài động vật đến vậy. Nhìn bé chơi vui thế này thì chị cũng mừng và vui lây.” <br/> <br/> Vận động ngoài trời không chỉ quan trọng với sự phát triển toàn diện của trẻ tự kỷ mà nó còn thật đáng quý, bởi lẽ vì những khó khăn về giao tiếp và hành vi, hiếm khi có dịp các em được thỏa thích nô đùa ở một không gian mới mẻ bên ngoài gia đình hay trường lớp. Trẻ em, dù là ai, cũng đều xứng đáng có được những trải nghiệm thích hợp và kỷ niệm tuổi thơ đáng nhớ.",ImageUrl="resource/images/news2.png"},
                new Chuongtrinh(){ID=6,MST="1",BannerUrl = "resource/images/banner-news11.png", Title = "Ngày hội dinh dưỡng cùng trẻ em Tiểu học Trà Vinh", ImageUrl = "resource/images/news11.jpg",Content = "Vào ngày 23 tháng Tư 2023, tại trường Tiểu học Hiếu Trung B (huyện Tiểu Cần, tỉnh Trà Vinh) đã diễn ra ngày hội dinh dưỡng với chủ đề “Bữa ăn Thông thái – Ươm mầm Tương lai” dành cho 405 em đang học tập tại ngôi trường này. Đây là sự kiện nằm trong hợp phần dinh dưỡng cho hơn 1.000 trẻ em có hoàn cảnh khó khăn tại tỉnh Trà Vinh, thuộc dự án “Cùng nhau hành động – Hỗ trợ nỗ lực phục hồi hậu COVID-19” mà Saigon Children’s Charity thực hiện với sự hỗ trợ của Prudential Việt Nam tại hai tỉnh Trà Vinh và Bình Thuận. <br/> <br/> Vào hôm ấy, các em đã được tìm hiểu về tháp dinh dưỡng cũng như giá trị dinh dưỡng của các món ăn thông qua trò chơi “Chiếc hộp bí mật”. Không những thế, các em còn được hướng dẫn về cách “tô màu cho bữa ăn” để học về đa dạng chất dinh dưỡng thông qua màu sắc các món trong bữa ăn, cũng như quy tắc phân bổ để có một bữa ăn cân bằng các nhóm chất. Sự kiện còn có sự tham gia của hơn 100 phụ huynh trong hoạt động hội thảo cùng chuyên gia, qua đó phụ huynh được chia sẻ các quy tắc cơ bản để xây dựng bữa ăn lành mạnh, dinh dưỡng cho trẻ. <br/> <br/> Trong suốt 55 ngày, từ tháng Ba cho đến hết năm học 2022 – 2023, 64.900 bữa phụ đã được hỗ trợ cho 1.180 học sinh mẫu giáo và tiểu học tại huyện Tiểu Cần, bao gồm thực hiện tại 4 trường, trong đó có hai trường mẫu giáo (Hiếu Tử, Hùng Hoà) và hai trường tiểu học (Hiếu Tử C, Hiếu Trung B). Vào mỗi buổi sáng, những chuyến xe chở đầy thức ăn sẽ đáp tại sân trường, các thầy cô phụ trách nhận nhiệm vụ phân loại và sắp xếp ngay ngắn để đảm bảo có đủ khẩu phần theo từng lớp học, từng loại chay và mặn khác nhau. Những lớp nhỏ sẽ được thầy cô đưa suất ăn đến tận lớp học; trong khi các em lớn hơn sẽ cử đại diện đến nơi tập trung và mang chúng về lớp cho các bạn vào lúc 9g sáng."},
                new Chuongtrinh(){ID=7,MST = "1", BannerUrl = "resource/images/banner-news7.png", Title = "2 ngày 1 đêm tràn ngập thanh âm tuổi trẻ của các bạn học sinh",ImageUrl = "resource/images/news7.png",Content="Cuối tháng 7 này, Trại hè Sinh viên 2023 vừa được diễn ra tại Đồng Nai dành cho sinh viên đang và đã nằm trong chương trình học bổng của saigonchildren ở cấp bậc đại học. Như mọi năm, Trại hè như một hoạt động ngoại khóa để các bạn có cơ hội tham gia vui chơi, hình thành các kỹ năng và mối quan hệ bạn bè mới; cũng như để gạt đi nỗi âu lo trong cuộc sống và tìm thấy nơi thể hiện sự nhiệt huyết tuổi trẻ vốn có của mình. <br/> <br/> Chủ đề trại năm nay mang tên “Happy Bridges” – trở thành chiếc cầu nối giữa các cá nhân xa lạ lại gần nhau hơn. Vì thế trong lần tổ chức này, các trại sinh đã cùng lăn xả hết mình với những thử thách được đề ra, với mục đích phát huy tinh thần đoàn kết đồng đội và văn hóa tập thể.<br/> <br/> Cuộc chơi khởi động bằng hoạt động dựng và trang trí lều trại bằng bóng bay, sáng tạo bảng tên nhóm thật phong cách và trình bày ý nghĩa của chúng trước Ban giám khảo để nhận các số điểm đầu tiên. Sự háo hức được tiếp tục khi tiến tới Lễ khai mạc và tiến hành các nghi thức đặc trưng, bao gồm vẽ mặt màu và nhảy theo bài hát chủ đề trại."},
                new Chuongtrinh(){ID=8,MST = "1", BannerUrl = "resource/images/banner-news8.png", Title = "Tham gia chuyến trải nghiệm thực tế giữa kì nghỉ hè", ImageUrl = "resource/images/news8.png",Content = "Hiếm khi có dịp được chứng kiến tận mắt quy trình thực hiện những sản phẩm thủ công ngoài đời thực, vì thế các bạn lớp tiếng Anh và Vi tính của trường Thăng Long đã thực sự có một trải nghiệm thú vị tại Trung tâm Giáo dục nghề nghiệp cho người khuyết tật và trẻ mồ côi (huyện Hóc Môn) vào giữa tháng 7 vừa qua. <br/> <br/> Trong dịp ghé thăm, 85 học sinh được chia thành 5 nhóm, lần lượt trải nghiệm những ngành nghề mà các bạn khuyết tật/mồ côi được đào tạo tại đây. Ở lớp may công nghiệp, các bạn được hướng dẫn đo cắt vải theo khuôn rập, tạo hình một số mẫu quần áo cơ bản, và quan sát học viên thực hiện những đường khâu tỉ mỉ để hoàn thiện một sản phẩm may mặc. Lớp vẽ tranh sơn dầu được đứng lớp bởi một thầy giáo kinh nghiệm, và thầy đã chia sẻ về chất liệu sơn dầu, cách tìm bố cục, đề tài, phác thảo và phối màu đơn giản.<br/> <br/> Một khu vườn nhỏ bên ngoài là không gian cho lớp thực hành nông nghiệp. Tại đây, các em có cơ hội quan sát kỹ thuật trồng và chăm sóc nấm, tham quan vườn hoa lan, và hăng hái phát quang cây cỏ, bụi rậm, để thông thoáng cảnh quan xung quanh khuôn viên. Ngoài ra, theo quan sát, học sinh đặc biệt yêu thích lớp hoa đất sét, nơi các em được tự tay nhào nặn bột, định hình từng cánh hoa thật khéo léo để có thể mô phỏng y hệt hoa tươi; và lớp kim hoàn, khi các em sử dụng công cụ chuyên dụng để chế tạo những món trang sức nho nhỏ dưới sự trợ giúp của các bạn học viên. Trường Phương Ninh bao gồm 1 cơ sở chính và 2 điểm lẻ nhỏ, là nơi tập học của khoảng 600 em học sinh cấp 1 và cấp 2 mỗi năm. Cơ sở chính của trường – nơi sẽ đón nhận những lớp học mới, được xây dựng từ cuối thập niên 80. Theo chia sẻ của nhiều giáo viên và học sinh, từ nhiều năm nay, cơ sở vật chất tại nhà trường đã bắt đầu xuống cấp. Các bức tường bắt đầu xuất hiện các vết nứt, lớp sơn bong tróc, cấu trúc trụ cột bê tông bị xói mòn, và khu vực sân ngoài trời phủ đầy rong rêu nên dễ gây trơn trượt nguy hiểm.Ngoài ra, trường Phương Ninh tọa lạc ngay cạnh bên khu bảo tồn thiên nhiên ngập nước chưa được khai phá nhiều; vì thế mỗi khi vào mùa mưa, học sinh còn có thể bắt gặp cá và thậm chí là đỉa, vắt xuất hiện trong sân trường. Chưa kể, mái tôn dột và máng xói mục rữa khiến lớp học bị ngập nước và hệ quả là các em từng phải nghỉ học khoảng một tháng để tránh lụt. "}
            };
        }

